import * as api from "@/api/project/srm/dataBase/specialDistinction";
import { DICT_CODE } from '@/utils/constant';
import caiGou from '@/components/Select/xtpt/caigou'; //下拉组件

import throttle from 'lodash/throttle';
export default {
  components: {
    caiGou: caiGou
  },
  data: function data() {
    return {
      DICT_CODE: DICT_CODE,
      temp: {
        rowId: undefined
      },
      dialogStatus: '',
      dialogVisible: false,
      rules: {
        purchaseOrg: [{
          required: true,
          message: '采购组织不能为空',
          trigger: 'blur'
        }],
        distinctionName: [{
          required: true,
          message: '区分名称不能为空',
          trigger: 'blur'
        }]
      }
    };
  },
  methods: {
    //获取采购组织名称
    selectName: function selectName(val) {
      this.temp.purchaseOrgName = val;
    },
    handleClose: function handleClose() {
      this.$refs.temp.resetFields();
    },
    createData: throttle(function () {
      var _this = this;

      this.$refs.temp.validate(function (valid) {
        if (valid) {
          api.addRecord(_this.temp).then(function () {
            _this.$message({
              title: '成功',
              message: '新增成功',
              type: 'success',
              duration: 2000
            });

            _this.dialogVisible = false;

            _this.$emit('refresh');
          });
        } else {
          return false;
        }
      });
    }, 2000, {
      leading: true,
      trailing: false
    }),
    updateData: throttle(function () {
      var _this2 = this;

      api.updateRecord(this.temp.rowId, this.temp).then(function () {
        _this2.$message({
          title: '成功',
          message: '编辑成功',
          type: 'success',
          duration: 2000
        });

        _this2.dialogVisible = false;

        _this2.$emit('refresh');
      });
    }, 2000, {
      leading: true,
      trailing: false
    }),
    resetTemp: function resetTemp() {
      this.temp = {
        purchaseOrg: undefined,
        distinctionCode: undefined,
        distinctionName: undefined,
        remark: undefined
      };
    }
  }
};
