import request from '@/utils/request';
export function queryRecords(query) {
  return request({
    url: '/easDevice/dicts',
    method: 'get',
    params: query
  });
}
export function getRecord(dcitCode) {
  return request({
    url: "/easDevice/dicts/".concat(dcitCode),
    method: 'get'
  });
}
export function addRecord(data) {
  return request({
    url: '/easDevice/dicts',
    method: 'post',
    data: data
  });
}
export function updateRecord(supplierCode, data) {
  return request({
    url: "/easDevice/dicts/".concat(supplierCode),
    method: 'patch',
    data: data
  });
}
export function deleteRecord(supplierCode) {
  return request({
    url: "/easDevice/dicts/".concat(supplierCode),
    method: 'delete'
  });
}
