import Tree from '@/components/Tree'; // Secondary package based on el-pagination

import Pagination from '@/components/Pagination'; // import { selectlists } from '@/api/system/organization'
// import * as api from '@/api/data-base/work-center'

import { queryWorkcenters } from '@/api/project/eas/database/groups';
export default {
  name: 'workSelect',
  components: {
    Pagination: Pagination,
    Tree: Tree
  },
  props: ['unMutiple', 'title', 'propName', 'filterWorkCenterCode', 'propCode', 'required'],
  data: function data() {
    return {
      activeContent: 'content1',
      treeType: 'org',
      treeParams: {
        orgCode: null
      },
      dialogVisible: false,
      list: [],
      subList: [],
      total: 0,
      listLoading: true,
      listQuery: {
        currentPage: 1,
        pageSize: 10,
        page: 1,
        orgId: null,
        workCenterCode: null,
        workCenterName: null
      },
      selectedRows: [],
      currentSelectedRow: null,
      treeStyleObj: {
        height: '437px'
      }
    };
  },
  created: function created() {
    this.handleQuery();
  },
  activated: function activated() {
    this.handleQuery();
  },
  watch: {
    'listQuery.orgId': function listQueryOrgId(val) {
      if (val) {
        this.handleQuery();
      }
    },
    filterWorkCenterCode: function filterWorkCenterCode(val) {
      if (val) {
        this.handleQuery();
      }
    }
  },
  methods: {
    handleQuery: function handleQuery() {
      var _this = this;

      //查询工作中心
      this.listQuery.page = 1;
      queryWorkcenters(this.listQuery, this.filterWorkCenterCode).then(function (response) {
        _this.list = response.data.list;
        _this.total = response.data.pages.count;

        if (_this.subList) {
          _this.subList.forEach(function (items) {
            _this.list.forEach(function (item, index) {
              if (item.workCenterCode === items.workCenterCode) {
                _this.$nextTick(function () {
                  this.checked(index);
                });
              }
            });
          });
        }
      });
    },
    selectAll: function selectAll(val) {
      var _this2 = this;

      // 全选
      if (val.length == 0) {
        this.$refs.tb.clearSelection();
        this.subList = this.subList.filter(function (x) {
          return _this2.list.every(function (y) {
            return y.workCenterCode !== x.workCenterCode;
          });
        });
        return false;
      }

      val.forEach(function (items, indexv) {
        var isSelected = false;

        _this2.subList.forEach(function (item) {
          if (item.workCenterCode === items.workCenterCode) {
            isSelected = true;
          }
        });

        if (!isSelected) {
          _this2.subList.push(val[indexv]);
        }
      });
    },
    rowClick: function rowClick(val) {
      this.$refs.tb.toggleRowSelection(val); //单击行绑定点击事件

      this.subList.push(val);
    },
    selectChange: function selectChange(val) {
      //备选项选中行
      if (this.unMutiple) {
        if (val.length > 1) {
          this.$refs.tb.clearSelection(); //清除其他行的选中

          this.$refs.tb.toggleRowSelection(val[val.length - 1], 'selected'); //单击行绑定点击事件
        } else if (val.length === 1) {
          this.selectedRows = val;
          this.currentSelectedRow = val[val.length - 1];
        } else if (val.length === 0) {
          this.selectedRows = val;
          this.currentSelectedRow = null;
        }
      } else {
        this.selectedRows = val;
        this.currentSelectedRow = val[val.length - 1];
      }
    },
    selectMain: function selectMain(val, row) {
      var _this3 = this;

      var isuncheck = this.checkUnsel(val, row);

      if (!isuncheck) {
        if (this.unMutiple) {
          if (val.length == 1) {
            this.subList = val;
          } else {
            this.$message({
              title: '警告',
              message: '请选择一条信息',
              type: 'warning'
            });
          }
        } else {
          if (this.subList) {
            val.forEach(function (items, indexv) {
              var isSelected = false;

              _this3.subList.forEach(function (item) {
                if (item.workCenterCode === items.workCenterCode) {
                  isSelected = true;
                }
              });

              if (!isSelected) {
                _this3.subList.push(val[indexv]);
              }
            });
          } else {
            this.subList = val;
          }
        }
      } else {
        this.subList = this.subList.filter(function (item) {
          return item.workCenterCode !== row.workCenterCode;
        });
      }
    },
    //检查是否取消勾选
    checkUnsel: function checkUnsel(val, row) {
      var isuncheck = true;
      val.forEach(function (item) {
        if (item.workCenterCode === row.workCenterCode) {
          isuncheck = false;
        }
      });
      return isuncheck;
    },
    checked: function checked(i) {
      this.$refs.tb.toggleRowSelection(this.list[i], true);
    },
    personConfirm: function personConfirm() {
      if (this.required && this.subList) {
        if (this.subList.length === 0) {
          this.$message({
            type: 'warning',
            message: '请选择一条数据'
          });
        } else {
          this.$emit('workConfirm', this.subList, this.propCode, this.propName);
          this.dialogVisible = false;
        }
      } else {
        if (this.subList) {
          if (this.subList.length === 0) {
            this.$emit('workConfirm', [{
              workCenterCode: null,
              workCenterName: null
            }], this.propCode, this.propName);
          } else {
            this.$emit('workConfirm', this.subList, this.propCode, this.propName);
          }
        } else {
          this.$emit('workConfirm', [{
            workCenterCode: null,
            workCenterName: null
          }], this.propCode, this.propName);
        }

        this.dialogVisible = false;
      }

      this.subList = [];
      this.$refs.tb.clearSelection();
    },
    setTreeDatas: function setTreeDatas(id) {
      this.$set(this.listQuery, 'orgId', id);
    }
  }
};
