import request from '@/utils/request'; //查询检验标准

export function queryRecords(query) {
  return request({
    url: '/mes/inspection/standards',
    method: 'get',
    params: query
  });
} //新增检验标准

export function addRecord(data) {
  return request({
    url: '/mes/inspection/standards',
    method: 'post',
    data: data
  });
} //查询检验标准单明细

export function getRecord(inspectionCode) {
  return request({
    url: "/mes/inspection/standards/".concat(inspectionCode),
    method: 'get'
  });
} //更新检验标准单明细

export function updateRecord(inspectionCode, data) {
  return request({
    url: "/mes/inspection/standards/".concat(inspectionCode),
    method: 'patch',
    data: data
  });
} //删除检验标准

export function deleteRecord(inspectionCode) {
  return request({
    url: "/mes/inspection/standards/".concat(inspectionCode),
    method: 'delete'
  });
}
