import request from '@/utils/request';
export function queryRecords(query) {
  //查询采购订单
  return request({
    url: "/scc/".concat(query.supplierCode, "/orders"),
    method: 'get',
    params: query
  });
}
export function queryRecords1(query) {
  //查询采购订单明细
  return request({
    url: "/scc/".concat(query.supplierCode, "/orders/").concat(query.orderNumber, "/items"),
    method: 'get',
    params: query
  });
}
export function confirm(query) {
  //操作采购订单
  return request({
    url: "/scc/".concat(query.supplierCode, "/").concat(query.method, "/").concat(query.orderNumber),
    method: 'patch',
    params: query
  });
}
export function updateRecord(supplierCode, orderNumber, data1) {
  //批量更新采购订单明细
  return request({
    url: "/scc/".concat(supplierCode, "/orders/").concat(orderNumber, "/items"),
    method: 'patch',
    data: data1
  });
}
