import request from '@/utils/request'; // 查询能源分类列表

export function queryEnergy(query) {
  return request({
    url: "/ems/EnergyType/list",
    method: 'get',
    params: query
  });
} // 新增

export function createEnergy(data) {
  return request({
    url: "/ems/EnergyType/save",
    method: 'post',
    data: data
  });
} // 更新

export function updateEnergy(code, data) {
  return request({
    url: "/ems/EnergyType/updateByCode/".concat(code),
    method: 'patch',
    data: data
  });
} // getRecord

export function getByCord(query) {
  return request({
    url: "/ems/EnergyType/getByCode/".concat(query),
    method: 'get'
  });
} // 删除

export function deleteEnergy(data) {
  return request({
    url: "/ems/EnergyType/deleteByCodes",
    method: 'delete',
    data: data
  });
}
