import request from '@/utils/request'; // 查询点巡标准列表

export function queryRecords(query) {
  return request({
    url: "/easDevice/base/inspection",
    method: 'get',
    params: query
  });
} //查询点巡列表明细

export function queryDetails(inspectionCode) {
  return request({
    url: "/easDevice/base/inspection/".concat(inspectionCode, "/item"),
    method: 'get'
  });
} //新增

export function addRecord(data) {
  return request({
    url: "/easDevice/base/inspection",
    method: 'post',
    data: data
  });
} //查询点巡列表明细

export function searchDetails(inspectionCode) {
  return request({
    url: "/easDevice/base/inspection/".concat(inspectionCode, "/item"),
    method: 'get'
  });
} //创建点巡列表明细

export function addDetails(inspectionCode, data) {
  return request({
    url: "/easDevice/base/inspection/".concat(inspectionCode, "/item"),
    method: 'post',
    data: data
  });
} //更新点巡列表明细

export function patchDetails(inspectionCode, partCode, data) {
  return request({
    url: "/easDevice/base/inspection/".concat(inspectionCode, "/item/").concat(partCode),
    method: 'patch',
    data: data
  });
} //删除点巡列表明细

export function deleteDetails(inspectionCode, partCode) {
  return request({
    url: "/easDevice/base/inspection/".concat(inspectionCode, "/item/").concat(partCode),
    method: 'delete'
  });
} //编辑

export function updateRecord(inspectionCode, data) {
  return request({
    url: "/easDevice/base/inspection/".concat(inspectionCode),
    method: 'patch',
    data: data
  });
} //删除

export function deleteRecord(inspectionCode) {
  return request({
    url: "/easDevice/base/inspection/".concat(inspectionCode),
    method: 'delete'
  });
} // 查询设备类型

export function queryTypeCode(query) {
  return request({
    url: "/easDevice/base/deviceType",
    method: 'get',
    params: query
  });
} // 查询设备部位

export function queryPart(typeCode) {
  return request({
    url: "/easDevice/base/part/".concat(typeCode),
    method: 'get'
  });
}
