import request from '@/utils/request';
export function getData_a(query) {
  //查询报废申请单列表
  return request({
    url: "/iwms/plans/scrapOrders",
    method: 'get',
    params: query
  });
}
export function queryRecords(query) {
  //查询主表
  return request({
    url: '/iwms/accounts' + '/summarise',
    method: 'get',
    params: query
  });
}
export function creatData_a(query) {
  //创建报废申请单
  return request({
    url: "/iwms/plans/scrapOrders",
    method: 'post',
    data: query
  });
}
