import request from '@/utils/request'; // 查询设备

export function queryDevice(query) {
  return request({
    url: "/easDevice/file/device",
    method: 'get',
    params: query
  });
} // 查询设备部位

export function queryPart(typeCode) {
  return request({
    url: "/easDevice/base/part/".concat(typeCode),
    method: 'get'
  });
} //查询点检记录列表

export function queryRecords(query) {
  return request({
    url: '/easDevice/inspection/task/device',
    method: 'get',
    params: query
  });
} //查询单个点检记录

export function querySingeRecord(inspectionTaskCode, inspectionTaskRecordCode) {
  return request({
    url: "/easDevice/inspection/task/".concat(inspectionTaskCode, "/device/").concat(inspectionTaskRecordCode),
    method: 'get'
  });
} //更新点检记录

export function patchSingeRecord(inspectionTaskCode, inspectionTaskRecordCode, data) {
  return request({
    url: "/easDevice/inspection/task/".concat(inspectionTaskCode, "/device/").concat(inspectionTaskRecordCode),
    method: 'patch',
    data: data
  });
} //查询使用部门

export function getWorkCenter(query) {
  return request({
    url: '/easDevice/work-centers',
    method: 'get',
    params: query
  });
} // 创建报修单

export function addRepair(inspectionTaskRecordCode, data) {
  return request({
    url: "/easDevice/inspection/task/repair/".concat(inspectionTaskRecordCode),
    method: 'post',
    data: data
  });
} // 查询保修单

export function queryRepair(inspectionTaskRecordCode) {
  return request({
    url: "/easDevice/inspection/task/repair/".concat(inspectionTaskRecordCode),
    method: 'get'
  });
}
