import Pagination from "@/components/Pagination"; // Secondary package based on el-pagination

import { selectlists, creat, deletelist, updatalist } from "@/api/system/resource"; // import waves from "@/directive/waves"; // Waves directive
// import { parseTime } from "@/utils";
// import { selecttree } from "@/api/system/organization";

export default {
  name: "systemResource",
  components: {
    Pagination: Pagination
  },
  // directives: { waves },
  data: function data() {
    return {
      tableKey: 0,
      list: [],
      total: 0,
      listLoading: true,
      theight: 0,
      //表格高度
      listQuery: {
        currentPage: 1,
        pageSize: 15,
        page: 1
      },
      readonly: false,
      temp: {
        endpointCode: undefined,
        endpointName: undefined,
        url: undefined,
        method: undefined,
        remark: undefined
      },
      dialogFormVisible: false,
      dialogFormUser: false,
      dialogStatus: "",
      rules: {
        endpointCode: [{
          required: true,
          message: "资源编码不能为空",
          trigger: "blur"
        }, {
          max: 100,
          message: "组织编码长度不能超过100",
          trigger: "blur"
        }],
        endpointName: [{
          required: true,
          message: "资源名称不能为空",
          trigger: "blur"
        }, {
          max: 100,
          message: "编码名称长度不能超过100",
          trigger: "blur"
        }],
        method: [{
          required: true,
          message: this.$t("validate.required"),
          trigger: "blur"
        }],
        url: [{
          required: true,
          message: this.$t("validate.required"),
          trigger: "blur"
        }]
      },
      dialogPvVisible: false,
      pvData: []
    };
  },
  created: function created() {
    this.getList();
  },
  mounted: function mounted() {
    var _this = this;

    this.setTableHeight(); //表格高度自适应

    window.onresize = function () {
      _this.setTableHeight();
    };

    this.getList();
  },
  methods: {
    getList: function getList() {
      var _this2 = this;

      //查询数据
      this.listLoading = true;
      selectlists(this.listQuery).then(function (response) {
        _this2.list = response.data.list;
        _this2.total = response.data.pages.count; // Just to simulate the time of the request

        setTimeout(function () {
          _this2.listLoading = false;
        }, 1.5 * 100);
      });
    },
    //表格高度计算
    setTableHeight: function setTableHeight() {
      this.theight = this.$myFun.getSingleTbHeight();
    },
    handleFilter: function handleFilter() {
      //查询按钮
      this.getList();
    },
    resetTemp: function resetTemp() {
      //重置
      this.temp = {
        endpointCode: undefined,
        endpointName: undefined,
        url: undefined,
        method: undefined,
        remark: undefined
      };
    },
    handleCreate: function handleCreate() {
      var _this3 = this;

      this.resetTemp();
      this.dialogStatus = "create";
      this.dialogFormVisible = true;
      this.readonly = false; //可以编写

      this.$nextTick(function () {
        _this3.$refs["dataForm"].clearValidate();
      });
    },
    createData: function createData() {
      var _this4 = this;

      this.$refs["dataForm"].validate(function (valid) {
        if (valid) {
          creat(_this4.temp).then(function () {
            _this4.getList();

            _this4.dialogFormVisible = false;

            _this4.$message({
              title: "成功",
              message: "创建成功",
              type: "success",
              duration: 2000
            });
          });
        }
      });
    },
    handleUpdate: function handleUpdate() {
      var _this5 = this;

      if (this.selectlistRow && this.selectlistRow.length == 1) {
        this.readonly = true; //不可以编写

        this.temp = this.selectlistRow[0]; // copy obj

        this.dialogStatus = "update";
        this.dialogFormVisible = true;
        this.$nextTick(function () {
          _this5.$refs["dataForm"].clearValidate();
        });
      } else {
        this.$message({
          title: "警告",
          message: "请选择一条信息",
          type: "warning"
        });
      }
    },
    updateData: function updateData() {
      var _this6 = this;

      this.$refs["dataForm"].validate(function (valid) {
        if (valid) {
          updatalist(_this6.temp.endpointCode, _this6.temp).then(function () {
            _this6.getList();

            _this6.dialogFormVisible = false;

            _this6.$message({
              title: "成功",
              message: "更新成功",
              type: "success",
              duration: 2000
            });
          });
        }
      });
    },
    // 获取表格选中时的数据
    selectRow: function selectRow(val) {
      this.selectlistRow = val;
    },
    handleDelete: function handleDelete() {
      var _this7 = this;

      //删除列表
      if (this.selectlistRow && this.selectlistRow.length == 1) {
        var ids = [];
        this.selectlistRow.forEach(function (val, index) {
          ids[index] = val.endpointCode;
        });
        deletelist(ids).then(function () {
          _this7.getList(), _this7.$message({
            title: "成功",
            message: "删除成功",
            type: "success",
            duration: 2000
          });
        });
      } else {
        this.$message({
          title: "警告",
          message: "请选择一条信息",
          type: "warning"
        });
      }
    }
  }
};
