import * as api from '@/api/project/ems/deviceManagement/dataAcquisition/dataItem.js';
import Pagination from '@/components/Pagination';
import throttle from 'lodash/throttle';
export default {
  name: 'dataItem',
  components: {
    Pagination: Pagination
  },
  data: function data() {
    return {
      list: null,
      listQuery: {
        page: true,
        currentPage: 1,
        pageSize: 15,
        dataItemName: undefined,
        dataItemAddress: undefined
      },
      temp: {
        dataItemName: undefined,
        dataItemCode: undefined,
        dataItemAddress: undefined,
        dataGiveCode: undefined,
        baseData: undefined,
        highStandard: undefined,
        lowStandard: undefined,
        remark: undefined
      },
      lowMin: 0,
      dataItemCode: undefined,
      dialogFormVisible: false,
      dialogStatus: '',
      tableKey: 0,
      total: 0,
      theight: 0,
      //表格高度
      selectlistRow: null,
      rules: {
        dataItemName: [{
          required: true,
          message: '请输入采集项编码',
          trigger: 'blur'
        }, {
          max: 50,
          message: '长度不能超过50',
          trigger: 'blur'
        }],
        dataItemCode: [{
          required: true,
          message: '请输入采集项名称',
          trigger: 'blur'
        }, {
          max: 50,
          message: '长度不能超过50',
          trigger: 'blur'
        }],
        dataItemAddress: [{
          required: true,
          message: '请输入采集项地址',
          trigger: 'blur'
        }, {
          max: 50,
          message: '长度不能超过50',
          trigger: 'blur'
        }],
        dataGiveCode: [{
          required: true,
          message: '请输入数采方编码',
          trigger: 'blur'
        }, {
          max: 50,
          message: '长度不能超过50',
          trigger: 'blur'
        }],
        baseData: [{
          required: true,
          message: '请输入基础值',
          trigger: 'blur'
        }],
        highStandard: [{
          required: true,
          message: '请输入上限',
          trigger: 'blur'
        }],
        lowStandard: [{
          required: true,
          message: '请输入下限',
          trigger: 'blur'
        }],
        remark: [{
          max: 50,
          message: '长度不能超过50',
          trigger: 'blur'
        }]
      }
    };
  },
  mounted: function mounted() {
    this.setTableHeight();
    this.getList();
  },
  destroyed: function destroyed() {
    this.setTableHeight();
    this.getList();
  },
  methods: {
    getList: function getList() {
      var _this = this;

      api.queryDataItem(this.listQuery).then(function (res) {
        res.data.list.map(function (v, i) {
          v.serial = i + 1;
        });
        _this.list = res.data.list;
        _this.total = res.data.pages.count;
      });
    },
    lowChange: function lowChange(low) {
      if (low >= this.temp.baseData) {
        this.temp.baseData = low + 1;
      }
    },
    baseChange: function baseChange(base) {
      if (base <= this.temp.lowStandard) {
        this.lowMin = base + 1;
        this.temp.lowStandard = base + 1;
      }
    },
    handleQuery: function handleQuery() {
      this.listQuery.currentPage = 1;
      this.getList();
    },
    handleCreate: function handleCreate() {
      this.resetTemp();
      this.dialogStatus = 'create';
      this.dialogFormVisible = true;
    },
    create: throttle(function () {
      var _this2 = this;

      this.$refs.temp.validate(function (valid) {
        if (valid) {
          api.createDataItem(_this2.temp).then(function () {
            _this2.getList();

            _this2.dialogFormVisible = false;

            _this2.$notify({
              title: '成功',
              message: '新增成功',
              type: 'success',
              duration: 2000
            });
          });
        } else {
          return false;
        }
      });
    }, 2000, {
      leading: true,
      trailing: false
    }),
    handleUpdate: function handleUpdate() {
      //编辑弹窗
      if (this.selectlistRow && this.selectlistRow.length == 1) {
        this.dataItemCode = this.selectlistRow[0].dataItemCode;
        this.temp = JSON.parse(JSON.stringify(this.selectlistRow[0])); // copy obj

        this.dialogStatus = 'update';
        this.dialogFormVisible = true;
      } else {
        this.$message({
          title: '警告',
          message: '请选择一条信息',
          type: 'warning'
        });
      }
    },
    update: throttle(function () {
      var _this3 = this;

      //编辑
      this.$refs['temp'].validate(function (valid) {
        if (valid) {
          api.updateDataItem(_this3.dataItemCode, _this3.temp).then(function () {
            _this3.getList();

            _this3.dialogFormVisible = false;

            _this3.$message({
              title: '成功',
              message: '更新成功',
              type: 'success',
              duration: 2000
            });
          });
        }
      });
    }, 2000, {
      leading: true,
      trailing: false
    }),
    handleDelete: function handleDelete() {
      var _this4 = this;

      //删除列表
      if (this.selectlistRow && this.selectlistRow.length >= 1) {
        this.$confirm('此操作将删除所选中数据, 是否继续?', '提示', {
          confirmButtonText: '确定',
          cancelButtonText: '取消',
          type: 'warning'
        }).then(function () {
          var ids = [];

          _this4.selectlistRow.forEach(function (val, index) {
            ids[index] = val.dataItemCode;
          });

          api.deleteDataItem(ids).then(function () {
            _this4.getList(), _this4.$message({
              title: '成功',
              message: '删除成功',
              type: 'success',
              duration: 2000
            });
          });
        }).catch(function () {
          _this4.$message({
            type: 'info',
            message: '已取消删除'
          });
        });
      } else {
        this.$message({
          title: '警告',
          message: '请选择一条信息',
          type: 'warning'
        });
      }
    },
    deleteQuery: function deleteQuery() {
      // 清除查询条件
      this.listQuery.currentPage = 1;
      this.listQuery.dataItemName = undefined;
      this.listQuery.dataItemAddress = undefined;
      this.getList();
    },
    resetTemp: function resetTemp() {
      this.temp = {};
    },
    handleClose: function handleClose() {
      this.resetTemp();
      this.$refs.temp.resetFields();
    },
    setTableHeight: function setTableHeight() {
      //表格高度计算
      this.theight = this.$myFun.getSingleTbHeight();
    },
    selectRow: function selectRow(val) {
      // 获取表格选中时的数据
      this.selectlistRow = val;
    }
  }
};
