import "core-js/modules/es6.array.sort";
import "core-js/modules/es6.function.name";
//
//
//
//
//
//
//
export default {
  name: "pie",
  props: ['id', 'data', 'title', 'radius', 'roseType', 'center', 'label', 'itemStyle', 'color'],
  data: function data() {
    return {
      mycharts: undefined
    };
  },
  watch: {
    data: function data() {
      this.getPie();
    }
  },
  mounted: function mounted() {
    var _this = this;

    window.addEventListener('resize', function () {
      if (_this.mycharts) {
        _this.mycharts.resize();
      }
    });
    this.getPie();
  },
  methods: {
    getPie: function getPie() {
      if (!this.data) return false;
      var name = this.data.name ? this.data.name : '';
      var legend = this.data.legend ? this.data.legend : [];
      this.myChart = this.$echarts.init(document.getElementById(this.id));
      var option = {
        title: {
          text: this.title,
          textStyle: {
            color: '#23b8e8',
            fontSize: '14'
          }
        },
        tooltip: {
          trigger: 'item',
          formatter: '{b}: {c} ({d}%)',
          confine: true
        },
        legend: {
          orient: 'vertical',
          y: 'center',
          left: 5,
          itemHeight: 14,
          textStyle: {
            color: '#fff'
          },
          data: legend
        },
        series: [{
          name: name,
          type: 'pie',
          color: this.color,
          radius: this.radius,
          avoidLabelOverlap: false,
          label: {
            show: false
          },
          data: this.data.data,
          zlevel: 9
        }]
      };

      if (this.roseType) {
        option.series[0].roseType = this.roseType;
        this.data.data = this.data.data.sort(function (a, b) {
          return a.value - b.value;
        });
      }

      if (this.center) {
        option.series[0].center = this.center;
      }

      if (this.itemStyle) {
        option.series[0].itemStyle = this.itemStyle;
      }

      this.myChart.setOption(option, true);
    }
  }
};
