import * as api from '@/api/data-base/materiel';
import dictItemSelect from '@/components/Select/dictItemSelect.vue';
import Pagination from '@/components/Pagination';
import { DICT_CODE } from '@/utils/constant';
export default {
  name: 'materialSelect',
  components: {
    Pagination: Pagination,
    dictItemSelect: dictItemSelect
  },
  props: {
    // 父组件中已选记录集合
    currentSelect: {
      type: Array,
      default: function _default() {
        return [];
      }
    }
  },
  data: function data() {
    return {
      list: null,
      total: 0,
      options: [],
      DICT_CODE: DICT_CODE,
      supplierData: null,
      listQuery: {
        currentPage: 1,
        pageSize: 10
      },
      activeName2: 'first',
      listSelected: [],
      tableKey: 0,
      allowSelect: false
    };
  },
  mounted: function mounted() {
    this.getEquipmentData(); // this.suppierRecords()
  },
  methods: {
    handleQuery: function handleQuery() {
      this.listQuery.currentPage = 1;
      this.getEquipmentData();
      this.currentSelect = [];
    },
    selectList: function selectList(val) {
      this.listSelected = val;
      this.$emit('input', this.listSelected);
    },
    selectRow: function selectRow(val) {
      this.$refs.tb_bx.toggleRowSelection(val, 'selected');
    },
    getList: function getList() {
      var _this = this;

      api.queryRecords(this.listQuery).then(function (res) {
        _this.list = res.data.list;
        _this.total = res.data.pages.count;
      });
    },
    getData: function getData() {
      var _this2 = this;

      this.$refs.tb_bx.clearSelection();
      this.currentSelect.forEach(function (item) {
        _this2.list = _this2.list.filter(function (v) {
          return v.materielCode != item.materielCode;
        });
      });
      this.listSelected = [];
    },
    // 在备选中把已选打钩
    getEquipmentData: function getEquipmentData() {
      var _this3 = this;

      api.queryRecords(this.listQuery).then(function (res) {
        _this3.list = res.data.list;
        _this3.total = res.data.pages.count; // 已选中的勾选状态

        setTimeout(function () {
          _this3.getData();
        }, 0);
      });
    }
  }
};
