import request from '@/utils/request';
export function queryRecords(query) {
  //查询主表
  return request({
    url: '/iwms/plans' + '/salesOut' + '/instructs',
    method: 'get',
    params: query
  });
}
export function queryRecordscb(query) {
  //查询从表
  return request({
    url: '/iwms/outStocks',
    method: 'get',
    params: query
  });
}
export function Save(data) {
  //保存
  return request({
    url: '/iwms/outStocks',
    method: 'post',
    data: data
  });
}
export function queryRecordsmx(query) {
  //查询单个出库凭证明细列表
  return request({
    url: '/iwms/outStocks' + '/' + query.outOrderNumber + '/details',
    method: 'get',
    params: query
  });
}
export function queryRecordsdy(query) {
  //打印出库凭证明细列表
  return request({
    url: "/outStocks/".concat(query.outOrderNumber, "/details/").concat(query.rowNo),
    method: 'get',
    params: query
  });
}
export function deleteRecord(instructCode) {
  //删除
  return request({
    url: '/iwms/plans' + '/salesOut' + '/instructs' + '/' + instructCode,
    method: 'delete'
  });
}
export function updateRecord(instructCode, data) {
  //编辑接口
  return request({
    url: '/iwms/plans' + '/salesOut' + '/instructs' + '/' + instructCode,
    method: 'patch',
    data: data
  });
