export default {
  name: 'contentPerson',
  data: function data() {
    return {
      formList: {
        role: null,
        rolename: null,
        assignee: null,
        assigneename: null,
        defroles: null,
        defroleNames: null,
        defusers: null,
        defuserNames: null
      },
      activeCollNames: ['1'],
      allowMultiple: false
    };
  },
  computed: {},
  mounted: function mounted() {},
  methods: {
    //点击节点时初始化表单
    initForm: function initForm(data) {
      console.log('person', data);

      for (var key in this.formList) {
        for (var keym in data) {
          for (var keyact in data[keym]) {
            if (keyact === key) {
              this.$set(this.formList, key, data[keym][keyact]);
            }
          }
        }
      }
    },
    selectClick: function selectClick(i) {
      this.$emit('selectClick', i);
    },
    setPerson: function setPerson(val) {
      switch (val[1]) {
        case 0:
          {
            if (val[0][0]) {
              this.$set(this.formList, 'role', val[0][0].roleCode);
              this.$set(this.formList, 'rolename', val[0][0].roleName);
            } else {
              this.$set(this.formList, 'role', '');
              this.$set(this.formList, 'rolename', '');
            }

            break;
          }

        case 1:
          {
            if (val[0][0]) {
              this.$set(this.formList, 'assignee', val[0][0].employeeCode);
              this.$set(this.formList, 'assigneename', val[0][0].employeeName);
            } else {
              this.$set(this.formList, 'assignee', '');
              this.$set(this.formList, 'assigneename', '');
            }

            break;
          }

        case 2:
          {
            if (val[0][0]) {
              this.$set(this.formList, 'defroles', val[0][0].roleCode);
              this.$set(this.formList, 'defroleNames', val[0][0].roleName);
            } else {
              this.$set(this.formList, 'defroles', '');
              this.$set(this.formList, 'defroleNames', '');
            }

            break;
          }

        case 3:
          {
            if (val[0][0]) {
              this.$set(this.formList, 'defusers', val[0][0].employeeCode);
              this.$set(this.formList, 'defuserNames', val[0][0].employeeName);
            } else {
              this.$set(this.formList, 'defusers', '');
              this.$set(this.formList, 'defuserNames', '');
            }

            break;
          }
      }
    },
    setDisabled: function setDisabled(val) {
      if (val === "1") {
        this.allowMultiple = false;
      } else {
        this.allowMultiple = true;
      }
    }
  }
};
