import Pagination from "@/components/Pagination"; // import { mapState } from "vuex"

export default {
  name: "jsgl",
  components: {
    Pagination: Pagination
  },
  data: function data() {
    return {
      modalnum: null,
      //模板编号，非模板页面可删
      theight: 0,
      //表格高度
      isSingle: true,
      //表格是否单选 点击各按钮根据流程逻辑控制单多选
      list: [],
      total: 0,
      listQuery: {
        //查询
        page: true,
        currentPage: 1,
        pageSize: 15
      },
      currentSelectedRow: null,
      //当前选中的行
      selectedRows: null,
      //多选时选中的所有行
      tableKey: 0 //表格索引

    };
  },
  created: function created() {},
  computed: {// ...mapState({
    //     dt_role_type: state => state.dict.dt_role_type
    // })
  },
  mounted: function mounted() {
    // this.$store.dispatch("dict/getDicData", ["dt_role_type"]);
    this.getList();
  },
  methods: {
    //获取表格数据
    getList: function getList() {// api.selectrole(this.listQuery).then(response => {
      //     this.list = response.data.list
      //     this.total = response.data.pages.count
      //     // Just to simulate the time of the request
      //     setTimeout(() => {
      //         this.listLoading = false
      //     }, 1.5 * 100)
      // })
    },
    //表格查询
    handleQuery: function handleQuery() {
      this.listQuery.currentPage = 1;
      this.getList();
    },
    //表格显示条数变化
    handleSizeChange: function handleSizeChange(val) {
      this.listQuery.pageSize = val;
      this.getList();
    },
    //表格当前页变化
    handleCurrentChange: function handleCurrentChange(val) {
      this.listQuery.currentPage = val;
      this.getList();
    },
    handleCreate: function handleCreate() {//新增按钮单击事件方法
    },
    handleUpdate: function handleUpdate() {//编辑按钮单击事件方法
    },
    handleDelete: function handleDelete() {//删除按钮单击事件方法
    },
    // 表格选择框选中 注:参数为选中的所有行的数组
    selectChange: function selectChange(val) {
      if (this.isSingle) {
        if (val.length > 1) {
          this.$refs.tb.clearSelection(); //清除其他行的选中

          this.$refs.tb.toggleRowSelection(val[val.length - 1], "selected"); //单击行绑定点击事件
        } else if (val.length === 1) {
          this.selectedRows = val;
          this.currentSelectedRow = val[val.length - 1];
        }
      } else {
        this.selectedRows = val;
        this.currentSelectedRow = val[val.length - 1];
      }
    },
    //点击表格某一行
    rowClick: function rowClick(val) {
      this.$refs.tb.toggleRowSelection(val, "selected"); //单击行绑定点击事件
    }
  }
};
