import request from '@/utils/request'; //查询点检计划

export function queryRecords(query) {
  return request({
    url: '/easDevice/inspection/inspect',
    method: 'get',
    params: query
  });
} //新增点检计划

export function addRecords(data) {
  return request({
    url: '/easDevice/inspection/inspect',
    method: 'post',
    data: data
  });
} //删除点检计划

export function deleteRecords(inspectPlanCode, deviceCode) {
  //  inspection/inspect
  return request({
    url: "/easDevice/inspection/inspect/".concat(inspectPlanCode, "/plan/").concat(deviceCode),
    method: 'delete'
  });
} //查询单个点检计划

export function querySingleRecords(inspectPlanCode, deviceCode) {
  return request({
    url: "/easDevice/inspection/inspect/".concat(inspectPlanCode, "/plan/").concat(deviceCode),
    method: 'get'
  });
} //修改单个点检计划

export function patchSingleRecords(inspectPlanCode, deviceCode, data) {
  return request({
    url: "/easDevice/inspection/inspect/".concat(inspectPlanCode, "/plan/").concat(deviceCode),
    method: 'patch',
    data: data
  });
} // 查询检验记录单单明细列表

export function queryDetails(inspectOrderCode) {
  return request({
    url: "/easDevice/inspection/inspect/".concat(inspectOrderCode, "/details"),
    method: 'get'
  });
} //查询单个点检计划-点检

export function querySingleRecord(inspectPlanCode, deviceCode) {
  return request({
    url: "/easDevice/inspection/inspect/".concat(inspectPlanCode, "/plan/").concat(deviceCode, "/inspect"),
    method: 'get'
  });
}
