import request from '@/utils/request';
export function queryRecords(query) {
  //查询主表
  return request({
    url: '/iwms/plans' + '/' + 'pushOrders',
    method: 'get',
    params: query
  });
}
export function queryRecordsgw(workCenterCode) {
  //查询工位
  return request({
    url: '/base-modeling/work-centers' + '/' + workCenterCode + '/' + 'stations',
    method: 'get'
  });
}
export function addRecord(data) {
  //新增
  return request({
    url: '/iwms/plans' + '/' + 'pushOrders',
    method: 'post',
    data: data
  });
}
export function deleteRecord(pushOrderCode) {
  //删除
  return request({
    url: '/iwms/plans' + '/' + 'pushOrders' + '/' + pushOrderCode,
    method: 'delete'
  });
}
export function deleteRecorditems(pushOrderCode, data) {
  //删除明细
  return request({
    url: '/iwms/plans' + '/' + 'pushOrders' + '/' + pushOrderCode + '/details',
    method: 'delete',
    data: data
  });
}
export function queryRecorditems(query) {
  //查询明细
  return request({
    url: '/iwms/plans' + '/' + 'pushOrders' + '/' + query.pushOrderCode + '/details',
    method: 'get',
    params: query
  });
}
export function updateRecord(pushOrderCode, data) {
  //编辑接口
  return request({
    url: '/iwms/plans' + '/' + 'pushOrders' + '/' + pushOrderCode,
    method: 'patch',
    data: data
  });
}
