// 需求分工api
import request from '@/utils/request'; //需求分工信息查询

export function queryRecords(query) {
  return request({
    url: '/purchase/division',
    method: 'get',
    params: query
  });
} // 分工、发布、驳回、分类设置、打包、取消打包

export function updateRecords(packageNo, data) {
  return request({
    url: "/purchase/division/".concat(packageNo),
    method: 'patch',
    data: data
  });
} // 分工、发布、驳回、分类设置、打包、取消打包

export function handleRecords(data) {
  return request({
    url: "/purchase/division/handle",
    method: 'patch',
    data: data
  });
}
