import request from '@/utils/request'; //物料分类维护树查询

export function queryRecords(query) {
  return request({
    url: "/basic/materiel-classes",
    method: 'get',
    params: query
  });
} //物料分类维护（新增）

export function addRecord(data) {
  return request({
    url: "/basic/materiel-classes",
    method: 'post',
    data: data
  });
} //物料分类维护（编辑）

export function updateRecord(rowId, data) {
  return request({
    url: "/basic/materiel-classes/".concat(rowId),
    method: 'patch',
    data: data
  });
} //物料分类维护删除

export function deleteRecord(rowId) {
  return request({
    url: "/basic/materiel-classes/".concat(rowId),
    method: 'delete'
  });
} //物料分类维供应商资质列表查询

export function queryRecordSupplier(query) {
  return request({
    url: "/SRMsupplier/supplier/qualifications",
    method: 'get',
    params: query
  });
} //供应商迁移（迁移）

export function transfer(data) {
  return request({
    url: "/SRMsupplier/supplier/qualifications/",
    method: 'patch',
    data: data
  });
} //资质转入撤销

export function revoke(data) {
  return request({
    url: "/SRMsupplier/supplier/qualifications",
    method: 'delete',
    data: data
  });
}
