import { getRecord } from "@/api/data-base/dict";
export default {
  name: "DictItemSelect",
  props: {
    dictCode: {
      required: true,
      type: String
    },
    dictItemKey: null,
    placeholder: {
      type: String,
      default: ""
    },
    disabled: {
      type: Boolean,
      default: false
    },
    isMutiple: {
      type: Boolean,
      default: false
    }
  },
  model: {
    prop: "dictItemKey",
    event: "change"
  },
  data: function data() {
    return {
      options: [],
      // placeholder: this.placeholder,
      itemKey: this.dictItemKey
    };
  },
  watch: {
    dictItemKey: function dictItemKey() {
      this.itemKey = this.dictItemKey;
    }
  },
  created: function created() {
    this.getOptions();
  },
  methods: {
    getOptions: function getOptions() {
      var _this = this;

      getRecord(this.dictCode).then(function (res) {
        _this.options = res.data.dictItemDTOs;
      });
    },
    changeSelect: function changeSelect(val) {
      this.$emit("change", val);
    }
  }
};
