import request from '@/utils/request';
export function queryRecords(query) {
  //查询
  return request({
    url: "/mes/record/firstProStep",
    method: 'get',
    params: query
  });
}
export function getRecord(workOrderState) {
  //查询随工单
  return request({
    url: "/mes/plan/work/".concat(workOrderState, "/state"),
    method: 'get'
  });
}
export function addRecord(data) {
  //创建首工序报工
  return request({
    url: "/mes/record/firstProStep",
    method: 'post',
    data: data
  });
}
export function deleteRecord(type, id_) {
  //删除首工序报工
  return request({
    url: "/mes/record/firstProStep/".concat(type, "/").concat(id_),
    method: 'delete'
  });
}
export function querylb(userName, accessTypeCode, metaDataType) {
  //查询工位
  return request({
    url: "/keyguard/users/".concat(userName, "/datas/accessTypeCode=").concat(accessTypeCode, "&metaDataType=").concat(metaDataType),
    method: 'get'
  });
}
