import request from '@/utils/request'; // 查询维修单

export function queryRecords(query) {
  return request({
    url: "/easDevice/maintain/workOrder",
    method: 'get',
    params: query
  });
} // 查询故障代码列表

export function faultCodeRecords(query) {
  return request({
    url: "/easDevice/fault/register",
    method: 'get',
    params: query
  });
} // 新增维修单

export function addRecords(data) {
  return request({
    url: "/easDevice/maintain/workOrder",
    method: 'post',
    data: data
  });
} //根据维修工单号查询维修工单

export function detailsRecords(workOrderNumber) {
  return request({
    url: "/easDevice/maintain/workOrder/".concat(workOrderNumber),
    method: 'get'
  });
} //查询维修人员

