import request from "@/utils/request"; //查询手工拉料单列表

export function queryRecords(query) {
  return request({
    url: "/iwms/plans/pullOrders",
    method: "get",
    params: query
  });
} //创建手工拉料单

export function createRecords(data) {
  return request({
    url: "/iwms/plans/pullOrders",
    method: "post",
    data: data
  });
} //查询单个手工拉料单

export function queryRowRecords(pullOrderCode) {
  return request({
    url: "/iwms/plans/pullOrders/".concat(pullOrderCode),
    method: "get"
  });
} //删除手工拉料单

export function deleteRecords(pullOrderCode) {
  return request({
    url: "/iwms/plans/pullOrders/".concat(pullOrderCode),
    method: "delete"
  });
} //更新手工拉料单

export function updateRecords(pullOrderCode, data) {
  return request({
    url: "/iwms/plans/pullOrders/".concat(pullOrderCode),
    method: "patch",
    data: data
  });
} //删除单条仓库规则

export function deleteRecordsDetail(pullOrderCode) {
  return request({
    url: "/iwms/plans/pullOrders/".concat(pullOrderCode, "/details"),
    method: "delete"
  });
}
