export default {
  name: 'tabWGcost',
  props: ['tabWGcostList'],
  data: function data() {
    return {
      type: null,
      tableData: []
    };
  },
  watch: {
    tabWGcostList: function tabWGcostList(val) {
      if (this.type == 'add') {
        var arr = JSON.parse(JSON.stringify(val));
        arr.forEach(function (i) {
          i.actuarialUnitPrice = i.unitPrice ? i.unitPrice : 0; //单价精算

          i.actuarialConsumptionQuota = i.consumptionQuota ? i.consumptionQuota : 0; //定额精算

          i.actuarialRejectRate = i.rejectRate ? i.rejectRate : 0; //废品率精算

          i.actuarialSubtotalCost = i.actuarialUnitPrice * i.actuarialConsumptionQuota || 0; //小计精算

          i.actuarialWasteLoss = i.actuarialUnitPrice * i.actuarialConsumptionQuota * i.actuarialRejectRate || 0; //废品损失精算
        });
        this.tableData = arr;
      } else {
        var _arr = JSON.parse(JSON.stringify(val));

        this.tableData = _arr;
      }
    }
  },
  methods: {
    checkValue: function checkValue(val) {
      if (isNaN(val) || val == Infinity) {
        return undefined;
      } else {
        if (val.toString().indexOf('.') !== -1) {
          val = val.toFixed(2);
        }

        return val;
      }
    },
    getSummaries: function getSummaries(param) {
      var _this = this;

      var columns = param.columns,
          data = param.data;
      var sums = [];
      columns.forEach(function (column, index) {
        if (index === 0) {
          sums[index] = '总计';
          return;
        }

        var values = data.map(function (item) {
          return Number(item[column.property]);
        });

        if (!values.every(function (value) {
          return isNaN(value);
        })) {
          if (column.property == 'subpartName' || column.property == 'actuarialUnitPrice' || column.property == 'actuarialConsumptionQuota' || column.property == 'actuarialSubtotalCost' || column.property == 'actuarialRejectRate' || column.property == 'actuarialWasteLoss') {
            sums[index] = values.reduce(function (prev, curr) {
              var value = Number(curr);

              if (!isNaN(value)) {
                return prev + curr;
              } else {
                return prev;
              }
            }, 0); // sums[index] += ' 元'
          } else {
            sums[index] = '--';
          }
        } else {
          sums[index] = 0;
        }
      });
      columns.forEach(function (val, index) {
        //小计精算
        if (val.property == 'actuarialSubtotalCost') {
          _this.$store.dispatch('getData/getWgSums', sums[index]);
        } //废品损失精算


        if (val.property == 'actuarialWasteLoss') {
          _this.$store.dispatch('getData/getDataLoss', sums[index]);
        }
      });
      return sums;
    },
    getPriceCost: function getPriceCost(row) {
      var _this2 = this;

      this.tableData.forEach(function (i) {
        if (i.rowId == row.rowId) {
          i.actuarialSubtotalCost = _this2.checkValue(i.actuarialUnitPrice * i.actuarialConsumptionQuota); //小计精算

          i.actuarialWasteLoss = _this2.checkValue(i.actuarialUnitPrice * i.actuarialConsumptionQuota * i.actuarialRejectRate); //废品损失精算
        }
      });
    },
    initData: function initData(val) {
      this.type = val;
      this.tableData = [];
    }
  }
};
