import request from '@/utils/request';
export function queryRecords(query) {
  //查询主表
  return request({
    url: '/iwms/allocations',
    method: 'get',
    params: query
  });
}
export function getRecord(query) {
  //查询明细
  return request({
    url: '/iwms/allocations' + '/' + query.allocNumber + '/items',
    method: 'get',
    params: query
  });
}
export function addRecord(data) {
  //新增
  return request({
    url: '/iwms/allocations',
    method: 'post',
    data: data
  });
}
export function operation(allocNumber, method) {
  //操作
  return request({
    url: '/iwms/allocations' + '/' + allocNumber + '/' + method,
    method: 'patch'
  });
}
export function updateRecord(allocNumber, data) {
  //更新
  return request({
    url: '/iwms/allocations' + '/' + allocNumber,
    method: 'patch',
    data: data
  });
}
export function deleteRecord(allocNumber) {
  //删除
  return request({
    url: '/iwms/allocations' + '/' + allocNumber,
    method: 'delete'
  });
} //获取区位

export function getArea(warehouseCode) {
  return request({
    url: "/base-modeling/warehouses/".concat(warehouseCode, "/areas"),
    method: 'get'
  });
} //获取储位

export function getLocator(warehouseCode, areaCode) {
  return request({
    url: "/base-modeling/warehouses/".concat(warehouseCode, "/areas/").concat(areaCode, "/locations"),
    method: 'get'
  });
}
