import request from '@/utils/request'; //零件报价通知查询

export function queryRecords(query) {
  return request({
    url: "/business/quote-notification",
    method: 'get',
    params: query
  });
} // 零件报价通知新增

export function createRecords(data) {
  return request({
    url: '/business/quote-notification',
    method: 'post',
    data: data
  });
} // 零件报价通知编辑

export function updateRecords(rowId, data) {
  return request({
    url: "/business/quote-notification/".concat(rowId),
    method: 'patch',
    data: data
  });
} //零件报价通知删除

export function deleteRecord(rowId) {
  return request({
    url: "/business/quote-notification/".concat(rowId),
    method: 'delete'
  });
} // 零件报价通知发布确认二次报价

export function confirmRecords(data) {
  return request({
    url: "/business/quote-notification/oper",
    method: 'patch',
    data: data
  });
}
