import request from '@/utils/request';
export function queryRecords(query) {
  //查询主表
  return request({
    url: "/iwms/plan/purchases",
    method: 'get',
    params: query
  });
}
export function queryRecordsmx(query, orderNumber) {
  //查询从表
  return request({
    url: "/iwms/plan/purchases/".concat(orderNumber, "/items"),
    method: 'get',
    params: query
  });
}
export function addRecords(data) {
  //保存
  return request({
    url: "/iwms/outStocks",
    method: 'post',
    data: data
  });
}
export function query(query) {
  //凭证号
  return request({
    url: "/iwms/outStocks",
    method: 'get',
    params: query
  });
}
export function queryDetails(query, outOrderNumber) {
  //凭证号明细
  return request({
    url: "/iwms/outStocks/".concat(outOrderNumber, "/details"),
    method: 'get',
    params: query
  });
} //获取区位

export function getArea(warehouseCode) {
  return request({
    url: "/base-modeling/warehouses/".concat(warehouseCode, "/areas"),
    method: 'get'
  });
} //获取储位

export function getLocator(warehouseCode, areaCode) {
  return request({
    url: "/base-modeling/warehouses/".concat(warehouseCode, "/areas/").concat(areaCode, "/locations"),
    method: 'get'
  });
}
export function recommend(query) {
  //库区库位推荐
  return request({
    url: "/iwms/ruleExecution/locationPropose",
    method: 'POST',
    data: query
  });
}
