import request from '@/utils/request'; // 业务逻辑
// 工作中心 => 对应工位列表 => 工位 => 对应工位设备列表
// 工作中心列表

export function getWorkCenterList() {
  return request({
    url: '/mes/work-centers',
    method: 'get' // params: query

  });
} // 工作中心对应工位列表

export function getStationList(query) {
  return request({
    url: "/mes/work-centers/stations",
    method: 'get',
    params: query
  });
} // 查询工位产品列表

export function getStationEquipmentList(data, stationCode) {
  return request({
    url: "/mes/work-centers/stations/".concat(stationCode, "/product"),
    method: 'get',
    params: data
  });
} // 添加工位产品

export function addStationEquipmentList(data, stationCode) {
  return request({
    url: "/mes/work-centers/stations/".concat(stationCode, "/product"),
    method: 'post',
    data: data
  });
} // 删除工位产品

export function DeleteStationEquipmentList(data, stationCode) {
  return request({
    url: "/mes/work-centers/stations/".concat(stationCode, "/product"),
    method: 'delete',
    data: [data.rowId]
  });
} // 更新工位产品

export function patchStationEquipmentList(data, stationCode) {
  return request({
    url: "/mes/work-centers/stations/".concat(stationCode, "/product"),
    method: 'patch',
    data: data
  });
} // 查询产品检验列表

export function getProductCheckList(data, stationCode, productCode) {
  return request({
    url: "/mes/work-centers/stations/".concat(stationCode, "/product/").concat(productCode, "/inspection"),
    method: 'get',
    params: data
  });
} // 添加产品检验项

export function addProductCheck(data, stationCode, productCode) {
  return request({
    url: "/mes/work-centers/stations/".concat(stationCode, "/product/").concat(productCode, "/inspection"),
    method: 'post',
    data: data
  });
} // 删除产品检验项

export function DeleteProductCheck(data, stationCode, productCode) {
  return request({
    url: "/mes/work-centers/stations/".concat(stationCode, "/product/").concat(productCode, "/inspection"),
    method: 'delete',
    data: [data.rowId]
  });
} // 更新产品检验项

export function patchProductCheck(data, stationCode, productCode) {
  return request({
    url: "/mes/work-centers/stations/".concat(stationCode, "/product/").concat(productCode, "/inspection"),
    method: 'patch',
    data: data
  });
}
