import { queryProjects } from "@/api/project/iot/IOT/project";
import mainBox from "./main";
import GRTabs from "./GRTabs";
export default {
  name: "gatewayQuery",
  components: {
    mainBox: mainBox,
    GRTabs: GRTabs
  },
  watch: {
    '$route.params': function $routeParams(newval) {
      if (newval) {
        this.pages = 'main';
        this.code = newval.code;
      }
    }
  },
  data: function data() {
    return {
      pages: 'main',
      code: this.$route.params.code,
      //一级页面项目编码
      row: undefined,
      // 二级页面选择的行
      projectList: null
    };
  },
  created: function created() {
    this.$store.dispatch('iotDict/getDicData', ['iot_networking_way', 'iot_state_gateway']);
    this.getProject();
  },
  methods: {
    setPage: function setPage(pages) {
      this.pages = pages;
    },
    setCode: function setCode(code) {
      this.code = code;
    },
    setRow: function setRow(row) {
      this.row = row;
    },
    getProject: function getProject() {
      var _this = this;

      queryProjects().then(function (res) {
        _this.projectList = res.data;
      });
    }
  }
};
