//设备绑定
import request from '@/utils/request'; // 查询设备绑定信息列表

export function queryDeviceBind(projectCode) {
  return request({
    url: "/iotIot/iot/".concat(projectCode, "/deviceBind"),
    method: 'get'
  });
} // 查询设备实时数据

export function queryDeviceBinds(projectCode, gatewayCode, deviceCode) {
  return request({
    url: "/iotIot/iot/".concat(projectCode, "/deviceBind/").concat(gatewayCode, "/").concat(deviceCode),
    method: 'get'
  });
} // 查询告警事件

export function queryDeviceAlarm(projectCode, gatewayCode, deviceCode, query) {
  return request({
    url: "/iotIot/iot/".concat(projectCode, "/deviceBind/").concat(gatewayCode, "/").concat(deviceCode, "/alarm"),
    method: 'get',
    params: query
  });
} // 设备实时数据批量交付

export function updateAllDeliver(projectCode, gatewayCode, deviceCode, data) {
  return request({
    url: "/iotIot/iot/".concat(projectCode, "/deviceBind/").concat(gatewayCode, "/").concat(deviceCode, "/allDeliver"),
    method: 'patch',
    data: data
  });
} // 设备实时数据交付

export function updateDeliver(projectCode, gatewayCode, deviceCode, pointCode) {
  return request({
    url: "/iotIot/iot/".concat(projectCode, "/deviceBind/").concat(gatewayCode, "/").concat(deviceCode, "/deliver/").concat(pointCode),
    method: 'patch'
  });
} // 查看曲线

export function queryCurve(projectCode, gatewayCode, deviceCode, pointCode, query) {
  return request({
    url: "/iotIot/iot/".concat(projectCode, "/deviceBind/").concat(gatewayCode, "/").concat(deviceCode, "/curve/").concat(pointCode),
    method: 'get',
    params: query
  });
}
