import request from '@/utils/request'; //  查询

export function queryModelDetail(query) {
  return request({
    url: "/ems/modelDetail/modelList",
    method: 'get',
    params: query
  });
} // 查询设备仪表采集项

export function queryModelDataItem(query) {
  return request({
    url: "/ems/ModelDataItem/relate",
    method: 'get',
    params: query
  });
} // 查询未关联数据

export function queryUnModelData(query) {
  return request({
    url: "/ems/ModelDataItem/unrelate",
    method: 'get',
    params: query
  });
} // 新增

export function createModelDetail(data) {
  return request({
    url: "/ems/modelDetail/modelSave",
    method: 'post',
    data: data
  });
} // 关联

export function relateSave(modelCode, data) {
  return request({
    url: "/ems/ModelDataItem/relateSave/".concat(modelCode),
    method: 'post',
    data: data
  });
} // 解除关联

export function deleteModelDataItem(data) {
  return request({
    url: "/ems/ModelDataItem/relateDelete",
    method: 'delete',
    data: data
  });
} // 删除

export function deleteModelDetail(data) {
  return request({
    url: "/ems/modelDetail/modelDelete",
    method: 'delete',
    data: data
  });
} // 更新

export function updateModelDetail(code, data) {
  return request({
    url: "/ems/modelDetail/modelUpdate/".concat(code),
    method: 'patch',
    data: data
  });
} // 查询能源分类

export function queryEnergyItem(query) {
  return request({
    url: "/ems/EnergyItem/ItemList",
    method: 'get',
    params: query
  });
} // 查询工作中心

export function queryWorkCenter(query) {
  return request({
    url: "ems/indicatorsSynthesisEnergy/Units",
    method: 'get',
    params: query
  });
} // 查询仪表型号

export function queryMeterType(query) {
  return request({
    url: "/ems/meterType/typeList",
    method: 'get',
    params: query
  });
}
