import request from '@/utils/request'; // 查询设备类型

export function queryTypeCode(query) {
  return request({
    url: "/easDevice/base/deviceType",
    method: 'get',
    params: query
  });
} // 查询设备台账

export function queryMainlist(query) {
  return request({
    url: "/easDevice/file/device",
    method: 'get',
    params: query
  });
} // 查询设备点检项

export function queryRecords(deviceCode) {
  return request({
    url: "/easDevice/file/device/".concat(deviceCode, "/inspection"),
    method: 'get'
  });
} // 新增设备点检项

export function addRecords(deviceCode, data) {
  return request({
    url: "/easDevice/file/device/".concat(deviceCode, "/inspection"),
    method: 'post',
    data: data
  });
} // 删除设备点检项

export function deleteRecords(deviceCode, inspectionCode) {
  return request({
    url: "/easDevice/file/device/".concat(deviceCode, "/inspection/").concat(inspectionCode),
    method: 'delete'
  });
} // 查询点巡检

export function queryBase(inspectionCode) {
  return request({
    url: "/easDevice/base/inspection/".concat(inspectionCode, "/item"),
    method: 'get'
  });
} // 预览

export function queryPreview(deviceCode) {
  return request({
    url: "/easDevice/file/device/".concat(deviceCode, "/inspection/preview"),
    method: 'get'
  });
}
