import request from '@/utils/request';
var state = {
  //获取评价模型数据
  dt_yw_pjmx: {
    url: '/supplier/supplierEvaluationModel',
    params: {
      method: 'combobox',
      level: 1,
      type: 'PAK1',
      purchaseOrg: null,
      category: 'PB20',
      state: 'PAS0'
    },
    data: []
  },
  //srm平台系列
  dt_cate_platformSeries: {
    url: '/basic/classificationDictionary',
    params: {
      parentCode: 'dt_srm_pb7'
    },
    data: []
  },
  //srm综合评价等级
  dt_cate_grade: {
    url: '/basic/classificationDictionary',
    params: {
      parentCode: 'dt_srm_pam'
    },
    data: []
  },
  //常量参数
  dt_const: {
    url: '/basic/constantParameters',
    params: {
      currentPage: 1,
      pageSize: 10,
      purchaseOrg: null
    },
    data: []
  }
};
var mutations = {
  getBisData: function getBisData(state, data) {
    state[data.dicCodes].data = data.res;
  },
  setPjmxPar: function setPjmxPar(state, data) {
    state['dt_yw_pjmx'].params.purchaseOrg = data; // console.log(state, data, 'setPjmxPar')
  }
};
var actions = {
  getBisData: function getBisData(context, dicCodes) {
    var _loop = function _loop(i) {
      if (state[dicCodes[i]].data.length == 0) {
        request({
          url: state[dicCodes[i]].url,
          method: 'get',
          params: state[dicCodes[i]].params
        }).then(function (res) {
          if (Array.isArray(res.data)) {
            res = res.data;
          } else if (Array.isArray(res.data.list)) {
            res = res.data.list;
          } else if (!Array.isArray(res)) {
            res = '';
          }

          context.commit('getBisData', {
            dicCodes: dicCodes[i],
            res: res
          });
        });
      }
    };

    for (var i = 0; i < dicCodes.length; i++) {
      _loop(i);
    }
  },
  setPjmxPar: function setPjmxPar(context, data) {
    context.commit('setPjmxPar', data);
  }
};
export default {
  namespaced: true,
  state: state,
  mutations: mutations,
  actions: actions
};
