import request from '@/utils/request';
export function queryRecords(query) {
  return request({
    url: '/base-modeling/customers',
    method: 'get',
    params: query
  });
}
export function getRecord(supplierCode) {
  return request({
    url: '/base-modeling/customers' + '/' + supplierCode,
    method: 'get'
  });
}
export function addRecord(data) {
  return request({
    url: '/base-modeling/customers',
    method: 'post',
    data: data
  });
}
export function updateRecord(supplierCode, data) {
  return request({
    url: '/base-modeling/customers' + '/' + supplierCode,
    method: 'patch',
    data: data
  });
}
export function deleteRecord(supplierCode) {
  return request({
    url: '/base-modeling/customers' + '/' + supplierCode,
    method: 'delete'
  });
}
