import request from '@/utils/request';
export default {
  name: 'warehouse-select',
  props: {
    orgCode: {
      required: true,
      type: String,
      default: ''
    },
    warehouseCode: {
      type: String,
      default: ''
    },
    placeholder: {
      type: String,
      default: ''
    },
    disabled: {
      type: Boolean,
      default: false
    }
  },
  model: {
    prop: 'warehouseCode',
    event: 'change'
  },
  data: function data() {
    return {
      options: [],
      selectValue: this.warehouseCode
    };
  },
  watch: {
    warehouseCode: {
      handler: function handler() {
        this.selectValue = this.warehouseCode;
      },
      immediate: true
    },
    orgCode: {
      handler: function handler(val) {
        if (val) {
          this.getOptions();
        } else {
          this.options = [];
        } // if (oldVal) {
        //     this.selectValue = undefined
        //     this.$emit('change', undefined)
        // }

      },
      immediate: true
    }
  },
  methods: {
    getOptions: function getOptions() {
      var _this = this;

      return request({
        url: '/base-modeling/warehouses',
        method: 'get',
        params: {
          orgCode: this.orgCode
        }
      }).then(function (res) {
        _this.options = res.data;
      });
    },
    changeSelect: function changeSelect(val) {
      this.$emit('change', val);
      this.$emit('input', val);
      var name = '';
      var org = this.options.filter(function (v) {
        return v.warehouseCode == val;
      }).find(function (value) {
        return value;
      });

      if (org) {
        name = org.warehouseName;
      }

      this.$emit('selectName', name);
    }
  }
};
