import { getAlarm } from "@/api/project/iot/IOT/ga";
import Pagination from "@/components/Pagination";
import throttle from 'lodash/throttle';
export default {
  name: "GAInfo",
  components: {
    Pagination: Pagination
  },
  props: ['projectList', 'gatewayList', 'code'],
  watch: {
    'listQuery.projectName': function listQueryProjectName(val) {
      if (!val) {
        this.listQuery.projectCode = undefined;
      }
    },
    'listQuery.gatewayName': function listQueryGatewayName(val) {
      if (!val) {
        this.listQuery.gatewayCode = undefined;
      }
    }
  },
  data: function data() {
    return {
      theight: 0,
      //表格高度
      listLoading: false,
      list: [],
      total: 0,
      listQuery: {
        page: true,
        currentPage: 1,
        pageSize: 15,
        gatewayCode: this.code,
        projectCode: undefined,
        projectName: undefined // gatewayCode: undefined,
        // gatewayName: undefined,

      },
      tableKey: 0 //表格索引

    };
  },
  mounted: function mounted() {
    var _this = this;

    this.setTableHeight();

    window.onresize = function () {
      _this.setTableHeight();
    };

    this.getList();
  },
  methods: {
    //获取表格数据
    getList: function getList() {
      var _this2 = this;

      this.listLoading = true;
      getAlarm(this.listQuery).then(function (res) {
        _this2.list = res.data.list;
        _this2.total = res.data.pages.count;
        setTimeout(function () {
          _this2.listLoading = false;
        }, 1.5 * 100);
      });
    },
    //表格查询
    handleQuery: throttle(function () {
      this.listQuery.currentPage = 1;
      this.getList();
    }, 2000, {
      leading: true,
      trailing: false
    }),
    toInfo: function toInfo(row) {
      this.$emit('setCode', row);
      this.$emit('setPage', 'GA-Info');
    },
    //表格高度计算
    setTableHeight: function setTableHeight() {
      this.theight = this.$myFun.getSingleTbHeight() - 30;
    },
    querySearchProject: function querySearchProject(queryString, cb) {
      var restaurants = this.projectList;
      var results = queryString ? restaurants.filter(this.createFilterProject(queryString)) : restaurants;
      cb(results);
    },
    createFilterProject: function createFilterProject(queryString) {
      return function (restaurant) {
        return restaurant.projectName.toLowerCase().indexOf(queryString.toLowerCase()) !== -1;
      };
    },
    handleSelectProject: function handleSelectProject(item) {
      this.listQuery.projectCode = item ? item.projectCode : undefined;
      this.listQuery.projectName = item ? item.projectName : undefined;
    },
    querySearchGateway: function querySearchGateway(queryString, cb) {
      var restaurants = this.gatewayList;
      var results = queryString ? restaurants.filter(this.createFilterGateway(queryString)) : restaurants;
      cb(results);
    },
    createFilterGateway: function createFilterGateway(queryString) {
      return function (restaurant) {
        return restaurant.gatewayName.toLowerCase().indexOf(queryString.toLowerCase()) !== -1;
      };
    },
    handleSelectGateway: function handleSelectGateway(item) {
      this.listQuery.gatewayCode = item ? item.gatewayCode : undefined;
      this.listQuery.gatewayName = item ? item.gatewayName : undefined;
    }
  }
};
