import request from '@/utils/request';
export function queryRecords(query) {
  return request({
    url: "/base-modeling/purchase-materiels",
    method: 'get',
    params: query
  });
}
export function getRecord(rowId) {
  return request({
    url: "/base-modeling/purchase-materiels/".concat(rowId),
    method: 'get'
  });
}
export function addRecord(data) {
  return request({
    url: "/base-modeling/purchase-materiels",
    method: 'post',
    data: data
  });
}
export function updateRecord(rowId, data) {
  return request({
    url: "/base-modeling/purchase-materiels/".concat(rowId),
    method: 'patch',
    data: data
  });
}
export function deleteRecord(rowId) {
  return request({
    url: "/base-modeling/purchase-materiels/".concat(rowId),
    method: 'delete'
  });
}
