import request from '@/utils/request'; // 查询能源分类列表

export function queryEnergyItem(query) {
  return request({
    url: "/ems/EnergyItem/ItemList",
    method: 'get',
    params: query
  });
} // 查询

export function queryRankId(code) {
  return request({
    url: "/ems/EnergyItem/ListByRankId/".concat(code),
    method: 'get'
  });
} // 新增

export function createEnergyItem(data) {
  return request({
    url: "/ems/EnergyItem/ItemSave",
    method: 'post',
    data: data
  });
} // 更新

export function updateEnergyItem(code, data) {
  return request({
    url: "/ems/EnergyItem/ItemUpdate/".concat(code),
    method: 'patch',
    data: data
  });
} // 删除

export function deleteEnergyItem(data) {
  return request({
    url: "/ems/EnergyItem/ItemDelete",
    method: 'delete',
    data: data
  });
} // 查询所选上级分项

export function queryEnergyItemPar(pid) {
  return request({
    url: "/ems/EnergyItem/ListByRankId/".concat(pid),
    method: 'get'
  });
}
