//项目信息
import request from '@/utils/request'; // 查询项目信息列表

export function queryProjects(query) {
  return request({
    url: "/iotIot/iot/project",
    method: 'get',
    params: query
  });
} // 查询单个项目信息

export function queryProject(projectCode) {
  return request({
    url: "/iotIot/iot/project/".concat(projectCode),
    method: 'get'
  });
} // 创建设备建模信息

export function addProject(data) {
  return request({
    url: "/iotIot/iot/project",
    method: 'post',
    data: data
  });
} //更新设备建模信息

export function updateProject(projectCode, data) {
  return request({
    url: "/iotIot/iot/project/".concat(projectCode),
    method: 'patch',
    data: data
  });
} // 删除设备建模信息

export function deleteProject(projectCode) {
  return request({
    url: "/iotIot/iot/project/".concat(projectCode),
    method: 'delete'
  });
} // 任务分配

export function queryBatch(projectCode, data) {
  return request({
    url: "/iotIot/iot/project/".concat(projectCode, "/delivery/batch"),
    method: 'patch',
    data: data
  });
} // 查询单个项目交付流程

export function queryOrderNumber(projectCode, orderNumber) {
  return request({
    url: "/iotIot/iot/project/".concat(projectCode, "/delivery/").concat(orderNumber),
    method: 'get'
  });
} // 更新项目交付流程

export function updateOrderNumber(projectCode, orderNumber, data) {
  return request({
    url: "/iotIot/iot/project/".concat(projectCode, "/delivery/").concat(orderNumber),
    method: 'patch',
    data: data
  });
}
