import * as api from "@/api/project/spm/sparePartsPurchase/requirementsGathering";
import { DICT_CODE } from '@/utils/constant';
import caiGou from '@/components/Select/xtpt/caigou'; //下拉组件

import personSelect from "@/components/Select/personSelect.vue";
export default {
  components: {
    caiGou: caiGou,
    personSelect: personSelect
  },
  data: function data() {
    return {
      DICT_CODE: DICT_CODE,
      dialogStatus: '',
      dialogVisible: false,
      dialogVisiblePerson: false,
      temp: {},
      parentRowsperson: [],
      rowIds: [],
      tempRul: {
        procurementGroupCode: [{
          required: true,
          message: '采购组不能为空',
          trigger: 'blur'
        }],
        planerName: [{
          required: true,
          message: '计划员不能为空',
          trigger: ['blur', 'change']
        }]
      }
    };
  },
  methods: {
    //获取采购组名称
    selectorgGroupName: function selectorgGroupName(val) {
      this.temp.procurementGroupName = val;
    },
    //请购人选择
    handlePerson: function handlePerson() {
      this.dialogVisiblePerson = true;
    },
    //请购人选择
    person: function person(val) {
      this.parentRowsperson = val;
    },
    //请购人确定
    confirmPerson: function confirmPerson() {
      if (this.parentRowsperson) {
        this.$set(this.temp, 'planerCode', this.parentRowsperson.employeeCode);
        this.$set(this.temp, 'planerName', this.parentRowsperson.employeeName);
        this.dialogVisiblePerson = false;
      } else {
        this.$message({
          title: '警告',
          message: '请选择一条信息',
          type: 'warning'
        });
      }
    },
    create: function create() {
      var _this = this;

      var sumUpdateDTO = {
        planerCode: this.temp.planerCode,
        planerName: this.temp.planerName,
        procurementGroupCode: this.temp.procurementGroupCode,
        procurementGroupName: this.temp.procurementGroupName,
        rowIds: this.rowIds
      };
      api.updateRecord(sumUpdateDTO).then(function () {
        _this.$message({
          title: '成功',
          message: '分工成功',
          type: 'success',
          duration: 2000
        });

        _this.dialogVisible = false;

        _this.$emit('refresh');
      });
    },
    resetTemp: function resetTemp() {
      this.temp = {
        procurementGroupCode: undefined,
        planerName: undefined
      };
    }
  }
};
