import request from '@/utils/request'; //价格审批单查询

export function queryRecords(query) {
  return request({
    url: "/business/price-approval",
    method: 'get',
    params: query
  });
} //价格审批单明细查询

export function queryRecordsItems(confirmationNo) {
  return request({
    url: "/business/price-confirmation/".concat(confirmationNo, "/item"),
    method: 'get'
  });
} // 价格审批单新增

export function createRecords(data) {
  return request({
    url: "/business/price-approval",
    method: 'post',
    data: data
  });
} // 价格审批单编辑

export function updateRecords(rowId, data) {
  return request({
    url: "/business/price-approval/".concat(rowId),
    method: 'patch',
    data: data
  });
} //价格审批单删除

export function deleteRecord(rowId) {
  return request({
    url: "/business/price-approval/".concat(rowId),
    method: 'delete'
  });
} // 价格审批单提交

export function submitRecords(data) {
  return request({
    url: "/business/price-approval/oper",
    method: 'patch',
    data: data
  });
}
