import request from '@/utils/request'; //常规付款方式查询

export function queryRecords(query) {
  return request({
    url: "/business/regularPayment",
    method: 'get',
    params: query
  });
} //常规付款方式维护（新增）

export function addRecord(data) {
  return request({
    url: "/business/regularPayment",
    method: 'post',
    data: data
  });
} //常规付款方式维护（编辑）

export function updateRecord(rowId, data) {
  return request({
    url: "/business/regularPayment/".concat(rowId),
    method: 'patch',
    data: data
  });
} //常规付款方式删除

export function deleteRecord(rowId) {
  return request({
    url: "/business/regularPayment/".concat(rowId),
    method: 'delete'
  });
} //常规付款方式维护（生效与失效）

export function updateEffect(rowId, data) {
  return request({
    url: "/business/regularPayment/".concat(rowId, "/state"),
    method: 'patch',
    data: data
  });
}
