import * as api from '@/api/data-base/materiel';
import dictItemSelect from '@/components/Select/dictItemSelect.vue';
import Pagination from '@/components/Pagination';
import { DICT_CODE } from '@/utils/constant';
export default {
  name: 'spareSelect',
  components: {
    Pagination: Pagination,
    dictItemSelect: dictItemSelect
  },
  props: {
    // 是否禁用选择设备类型（需要在父组件中关联进行控制时使用）
    deviceTypeDisable: {
      type: Boolean,
      default: false
    },
    // 父组件中已选记录集合
    listSelected: {
      type: Array,
      default: function _default() {
        return [];
      }
    }
  },
  data: function data() {
    return {
      // 设备列表（备选列表）
      list: [],
      // 设备类型
      options: [],
      DICT_CODE: DICT_CODE,
      total: 0,
      // 查询条件
      listQuery: {
        currentPage: 1,
        pageSize: 10
      },
      // 当前tab
      activeName2: 'first',
      // 当前弹框弹出后已选记录集合
      currentSelect: [],
      tableKey: 0
    };
  },
  mounted: function mounted() {
    this.$store.dispatch('eamDict/getDicData', ['dt_device_category']);
    this.getList();
  },
  methods: {
    getList: function getList() {
      var _this = this;

      this.listQuery.materielCodes = this.listSelected.map(function (item) {
        return item.materielCode;
      }).join(','); // 获取当前查询条件下的设备

      api.queryRecords(this.listQuery).then(function (res) {
        _this.list = res.data.list;
        _this.total = res.data.pages.count;
        setTimeout(function () {
          // 翻页后对当前已选打钩
          _this.getData();
        }, 0);
      });
    },
    handleQuery: function handleQuery() {
      // 翻页
      this.listQuery.currentPage = 1;
      this.getList();
    },
    checkUnsel: function checkUnsel(val, row) {
      // 当前选择记录是否已选
      var isuncheck = true;
      val.forEach(function (item) {
        if (item.materielCode == row.materielCode) {
          isuncheck = false;
        }
      });
      return isuncheck;
    },
    selectMain: function selectMain(val, row) {
      var _this2 = this;

      // 点击备选记录
      var isuncheck = this.checkUnsel(val, row);

      if (!isuncheck) {
        if (this.currentSelect) {
          val.forEach(function (items, indexv) {
            var isSelected = false;

            _this2.currentSelect.forEach(function (item) {
              if (item.materielCode == items.materielCode) {
                isSelected = true;
              }
            });

            if (!isSelected) {
              _this2.currentSelect.push(val[indexv]);
            }
          });
        } else {
          this.currentSelect = val;
        }
      } else {
        this.currentSelect = this.currentSelect.filter(function (item) {
          return item.materielCode != row.materielCode;
        });
      }
    },
    selectAll: function selectAll(val) {
      var _this3 = this;

      // 全选
      if (val.length == 0) {
        this.list.forEach(function (item) {
          _this3.currentSelect.forEach(function (item2, index) {
            if (item.materielCode == item2.materielCode) {
              _this3.currentSelect.splice(index, 1);
            }
          });
        });
        return;
      }

      val.forEach(function (items, indexv) {
        var isSelected = false;

        _this3.currentSelect.forEach(function (item) {
          if (item.materielCode == items.materielCode) {
            isSelected = true;
          }
        });

        if (!isSelected) {
          _this3.currentSelect.push(val[indexv]);
        }
      });
    },
    getData: function getData() {
      var _this4 = this;

      // 翻页后当前已选打钩
      if (this.currentSelect.length) {
        this.currentSelect.forEach(function (selected) {
          _this4.list.forEach(function (wait, index) {
            if (wait.materielCode == selected.materielCode) {
              _this4.$nextTick(function () {
                _this4.$refs.tb_bx.toggleRowSelection(_this4.list[index], true);
              });
            }
          });
        });
      }
    }
  }
};
