import request from '@/utils/request'; //查询维修计划

export function queryRecords(query) {
  return request({
    url: "/easDevice/maintain/plan/",
    method: 'get',
    params: query
  });
} // 查询供应商

export function getSuppliers(query) {
  return request({
    url: '/easDevice/suppliers',
    method: 'get',
    params: query
  });
} //创建维修计划

export function addRecords(data) {
  return request({
    url: "/easDevice/maintain/plan/",
    method: 'post',
    data: data
  });
} //修改维修计划

export function patchRecords(data) {
  return request({
    url: "/easDevice/maintain/plan/",
    method: 'patch',
    data: data
  });
} //修改维修班组

export function patchMaintainer(data) {
  return request({
    url: "/easDevice/maintain/plan/maintainer",
    method: 'patch',
    data: data
  });
} //修改维修班组

export function patchPlanTime(data) {
  return request({
    url: "/easDevice/maintain/plan/planTime",
    method: 'patch',
    data: data
  });
} //删除维修计划

export function deleteRecords(maintainPlanNumbers) {
  return request({
    url: "/easDevice/maintain/plan/",
    method: 'delete',
    data: maintainPlanNumbers
  });
} //派工

export function dispatch(maintainPlanNumbers) {
  return request({
    url: "/easDevice/maintain/plan/assign",
    method: 'patch',
    data: maintainPlanNumbers
  });
}
