import { queryRecords } from '@/api/data-base/warehouse';
import Pagination from '@/components/Pagination';
import orgSelect from '@/components/Select/orgSelect.vue';
import { DT_ORG_TYPE } from '@/utils/constant';
export default {
  name: 'reservoir-location',
  components: {
    Pagination: Pagination,
    orgSelect: orgSelect
  },
  data: function data() {
    return {
      list: null,
      total: 0,
      listQuery: {
        currentPage: 1,
        pageSize: 10,
        orgCode: undefined,
        warehouseCode: undefined
      },
      dialogStatus: '',
      tableKey: 0,
      tableKey1: 0,
      tableKey2: 0,
      DT_ORG_TYPE: DT_ORG_TYPE,
      rules: {}
    };
  },
  created: function created() {
    this.getList();
  },
  methods: {
    getList: function getList() {
      var _this = this;

      //查询组织
      queryRecords(this.listQuery).then(function (res) {
        _this.list = res.data.list;
        _this.total = res.data.pages.count;
      });
    },
    handleQuery: function handleQuery() {
      this.listQuery.currentPage = 1;
      this.getList();
    },
    selectRow: function selectRow(val) {
      //备选项选中行
      this.selectRowList = val[val.length - 1];

      if (val.length > 1) {
        this.$refs.tb.clearSelection();
        this.$refs.tb.toggleRowSelection(val.pop());
      }

      this.$emit('handSelectCkbmRows', this.selectRowList);
    }
  }
};
