import { queryDeviceAlarm } from "@/api/project/iot/MOM/ac";
import Pagination from "@/components/Pagination";
import throttle from 'lodash/throttle';
export default {
  name: "deviceAlarm",
  components: {
    Pagination: Pagination
  },
  props: ['projectList', 'deviceList'],
  watch: {
    'listQuery.projectName': function listQueryProjectName(val) {
      if (!val) {
        this.listQuery.projectCode = undefined;
      }
    },
    'listQuery.deviceName': function listQueryDeviceName(val) {
      if (!val) {
        this.listQuery.deviceCode = undefined;
      }
    },
    'listQuery.projectCode': function listQueryProjectCode(val) {
      if (!val) {
        this.listQuery.deviceName = undefined;
      } else {
        this.$emit('setDevice', val);
      }
    }
  },
  data: function data() {
    return {
      theight: 0,
      //表格高度
      listLoading: false,
      list: [],
      total: 0,
      listQuery: {
        page: true,
        currentPage: 1,
        pageSize: 15,
        projectName: undefined,
        projectCode: undefined,
        deviceCode: undefined,
        deviceName: undefined,
        alarmStartTime: undefined,
        alarmEndTime: undefined
      },
      tableKey: 0 //表格索引

    };
  },
  created: function created() {
    this.getNowDate();
  },
  mounted: function mounted() {
    var _this = this;

    this.setTableHeight();

    window.onresize = function () {
      _this.setTableHeight();
    };

    this.getList();
  },
  methods: {
    getNowDate: function getNowDate() {
      //获取当前时间
      var day = new Date();
      var now = new Date();
      day.setTime(day.getTime() - 7 * 24 * 60 * 60 * 1000);
      this.listQuery.alarmStartTime = getDate(day);
      this.listQuery.alarmEndTime = getDate(now);

      function getDate(day) {
        var year = day.getFullYear(); //得到年份

        var month = day.getMonth() + 1 > 10 ? day.getMonth() + 1 : '0' + (day.getMonth() + 1); //得到月份

        var date = day.getDate() > 10 ? day.getDate() : '0' + day.getDate(); //得到日期

        return "".concat(year, "-").concat(month, "-").concat(date);
      }
    },
    getList: function getList() {
      var _this2 = this;

      queryDeviceAlarm(this.listQuery).then(function (res) {
        _this2.list = res.data.list;
        _this2.total = res.data.pages.count;
      });
    },
    //表格查询
    handleQuery: throttle(function () {
      this.listQuery.currentPage = 1;
      this.getList();
    }, 2000, {
      leading: true,
      trailing: false
    }),
    toInfo: function toInfo(row) {
      this.$emit('setCode', row.deciveAlarmCode);
      this.$emit('setRow', row);
      this.$emit('setTime', this.listQuery);
      this.$emit('setPage', 'DA-Info');
    },
    //表格高度计算
    setTableHeight: function setTableHeight() {
      this.theight = this.$myFun.getSingleTbHeight() - 30;
    },
    querySearchProject: function querySearchProject(queryString, cb) {
      var restaurants = this.projectList;
      var results = queryString ? restaurants.filter(this.createFilterProject(queryString)) : restaurants;
      cb(results);
    },
    createFilterProject: function createFilterProject(queryString) {
      return function (restaurant) {
        return restaurant.projectName.toLowerCase().indexOf(queryString.toLowerCase()) !== -1;
      };
    },
    handleSelectProject: function handleSelectProject(item) {
      this.listQuery.projectCode = item ? item.projectCode : undefined;
      this.listQuery.projectName = item ? item.projectName : undefined;
    },
    querySearchDevice: function querySearchDevice(queryString, cb) {
      var restaurants = JSON.parse(JSON.stringify(this.deviceList));
      var results = queryString ? restaurants.filter(this.createFilterDevice(queryString)) : restaurants;
      cb(results);
    },
    createFilterDevice: function createFilterDevice(queryString) {
      return function (restaurant) {
        return restaurant.deviceName.toLowerCase().indexOf(queryString.toLowerCase()) !== -1;
      };
    },
    handleSelectDevice: function handleSelectDevice(item) {
      this.listQuery.deviceCode = item ? item.deviceCode : undefined;
      this.listQuery.deviceName = item ? item.deviceName : undefined;
    }
  }
};
