import * as api from '@/api/project/mes/dataBase/materiel';
import Pagination from '@/components/Pagination';
import dictItemSelect from '@/components/Select/dictItemSelect.vue';
import { DICT_CODE, TWO_STATE_OPTIONS } from '@/utils/constant';
export default {
  name: 'materiel',
  components: {
    Pagination: Pagination,
    dictItemSelect: dictItemSelect
  },
  data: function data() {
    return {
      list: null,
      total: 0,
      theight: 0,
      //表格高度
      dialogPrintVisible: false,
      selectlistRow: [],
      printData: {},
      //打印数据
      code: '',
      listQuery: {
        page: true,
        currentPage: 1,
        pageSize: 10,
        materielCode: undefined,
        materielName: undefined,
        materielType: undefined,
        materielClassify: undefined,
        materielGroup: undefined,
        specification: undefined,
        materialQuality: undefined,
        productionState: undefined
      },
      materielCode: undefined,
      selectCode: [],
      tableKey: 0,
      DICT_CODE: DICT_CODE,
      TWO_STATE_OPTIONS: TWO_STATE_OPTIONS
    };
  },
  created: function created() {
    this.getList();
  },
  mounted: function mounted() {
    var _this = this;

    this.setTableHeight(); //表格高度自适应

    window.onresize = function () {
      _this.setTableHeight();
    };

    this.getList();
  },
  methods: {
    getList: function getList() {
      var _this2 = this;

      api.queryRecords(this.listQuery).then(function (res) {
        _this2.list = res.data.list;
        _this2.total = res.data.pages.count;
      });
    },
    //表格高度计算
    setTableHeight: function setTableHeight() {
      this.theight = this.$myFun.getSingleTbHeight();
    },
    handleQuery: function handleQuery() {
      this.listQuery.currentPage = 1;
      this.getList();
    },
    // 获取表格选中时的数据
    selectRow: function selectRow(val) {
      this.selectedRows = val[val.length - 1];

      if (val.length > 1) {
        this.$refs.tb.clearSelection();
        this.$refs.tb.toggleRowSelection(val[val.length - 1]);
      }

      this.$refs.tb.setCurrentRow(this.selectedRows);
      this.$emit('handChange', this.selectedRows);
    }
  }
};
