export default {
  name: 'echartsCom',
  props: ['list'],
  // 图表数据
  data: function data() {
    return {
      legendData: [],
      // 图例数组
      xAxisData: [],
      // 横坐标日期
      series: [],
      // 折线图数据
      myChart: null // 初始化echarts实例

    };
  },
  mounted: function mounted() {
    var _this = this;

    window.onresize = function () {
      if (_this.myChart) {
        _this.myChart.resize();
      }
    };
  },
  methods: {
    // 父组件点击查询时调用
    showEcharts: function showEcharts() {
      var _this2 = this;

      // 异步获取父组件的数据
      setTimeout(function () {
        // 数据重置
        _this2.legendData = [];
        _this2.xAxisData = [];
        _this2.series = [];
        var deleteTime = [];

        if (_this2.list.length > 0) {
          _this2.myChart = _this2.$echarts.init(document.getElementById('chart'));

          _this2.list.map(function (item) {
            _this2.xAxisData.push(item.time);

            delete item.time;
            deleteTime.push(item);
          }); // console.log(this.xAxisData)


          _this2.legendData = Object.keys(deleteTime[0]); // console.log(this.legendData)

          _this2.legendData.map(function (item) {
            var obj = {
              name: item,
              type: 'line',
              stack: '总量',
              smooth: true,
              symbol: 'none',
              sampling: 'average',
              data: []
            };
            deleteTime.map(function (childItem) {
              obj.data = obj.data.concat(childItem[item]);
            });

            _this2.series.push(obj);
          });

          _this2.drawChart();
        }
      });
    },
    // 绘制折线图
    drawChart: function drawChart() {
      var option = {
        tooltip: {
          trigger: 'axis',
          position: function position(pt) {
            return [pt[0], '10%'];
          }
        },
        toolbox: {
          feature: {
            dataZoom: {
              yAxisIndex: 'none'
            },
            restore: {},
            saveAsImage: {}
          }
        },
        legend: {
          data: this.legendData
        },
        dataZoom: [{
          type: 'inside',
          start: 0,
          end: 100
        }, {
          start: 0,
          end: 10,
          handleIcon: 'M10.7,11.9v-1.3H9.3v1.3c-4.9,0.3-8.8,4.4-8.8,9.4c0,5,3.9,9.1,8.8,9.4v1.3h1.3v-1.3c4.9-0.3,8.8-4.4,8.8-9.4C19.5,16.3,15.6,12.2,10.7,11.9z M13.3,24.4H6.7V23h6.6V24.4z M13.3,19.6H6.7v-1.4h6.6V19.6z',
          handleSize: '80%',
          handleStyle: {
            color: '#fff',
            shadowBlur: 3,
            shadowColor: 'rgba(0, 0, 0, 0.6)',
            shadowOffsetX: 2,
            shadowOffsetY: 2
          }
        }],
        xAxis: {
          type: 'category',
          boundaryGap: false,
          data: this.xAxisData
        },
        yAxis: {
          type: 'value'
        },
        series: this.series // 必须指定第二个参数，折线图不合并

      };
      this.myChart.setOption(option, true);
    }
  }
};
