  computed: {// ...mapState({
    //     dt_materiel_classify: state => state.dict.dt_materiel_classify,
    //     dt_materiel_group: state => state.dict.dt_materiel_group,
    //     dt_materiel_type: state => state.dict.dt_materiel_type,
    // })
  },
  mounted: function mounted() {
    this.getList(); // this.$store.dispatch('dict/getDicData', ['dt_materiel_classify', 'dt_materiel_group', 'dt_materiel_type'])
  },
  methods: {
    checked: function checked(i) {
      this.$refs.tbselect.toggleRowSelection(this.list[i], true);
    },
    getData: function getData(val) {
      console.log(val, '99'); // this.listQuery.orgCode = val[0].orgCode

      this.$set(this.listQuery, 'orgCode', val[0].organizationCode);
    },
    getList: function getList() {
      var _this = this;

      api.queryRecordsMain(this.listQuery).then(function (res) {
        res = res.data;
        _this.list = res.list;
        _this.total = res.pages.count;

        if (_this.listSelected) {
          _this.listSelected.forEach(function (items) {
            _this.list.forEach(function (item, index) {
              if (item.materielCode === items.materielCode) {
                _this.$nextTick(function () {
                  this.checked(index);
                });
              }
            });
          });
        }
      });
    },
    handleQuery: function handleQuery() {
      this.listQuery.currentPage = 1;
      this.getList();
    },
    handleSizeChange: function handleSizeChange(val) {
      this.listQuery.pageSize = val;
      this.getList();
    },
    handleCurrentChange: function handleCurrentChange(val) {
      this.listQuery.currentPage = val;
      this.getList();
    },
    //检查是否取消勾选
    checkUnsel: function checkUnsel(val, row) {
      var isuncheck = true;
      val.forEach(function (item) {
        if (item.materielCode === row.materielCode) {
          isuncheck = false;
        }
      });
      return isuncheck;
    },
    selectMain: function selectMain(val, row) {
      var _this2 = this;

      if (this.unMutiple) {
        var isuncheck = this.checkUnsel(val, row);

        if (!isuncheck) {
          var arr = [];
          arr.push(val[val.length - 1]);
          this.listSelected = arr;

          if (val.length > 1) {
            this.$refs.tbselect.clearSelection();
            this.$refs.tbselect.toggleRowSelection(val[val.length - 1], 'selected');
          }
        } else {
          this.listSelected = [];
        }
      } else {
        var _isuncheck = this.checkUnsel(val, row);

        if (!_isuncheck) {
          if (this.listSelected) {
            val.forEach(function (items, indexv) {
              var isSelected = false;

              _this2.listSelected.forEach(function (item) {
                if (item.materielCode === items.materielCode) {
                  isSelected = true;
                }
              });

              if (!isSelected) {
                _this2.listSelected.push(val[indexv]);
              }
            });
          } else {
            this.listSelected = val;
          }
        } else {
          this.listSelected = this.listSelected.filter(function (item) {
            return item.materielCode !== row.materielCode;
          });
        }
      }
    },
    //选择确认
    handleConfirm: function handleConfirm() {
      this.$emit('handleDialogClick', this.listSelected);
      this.dialogVisible = false;
    },
    handlePreview: function handlePreview() {
      console.log('预览');
      this.$emit('handlePre', 'act', this.listSelected);
    }
  }
};
